mapper.update:
update fav set
usersid=#{usersid} , goodsid=#{goodsid} , addtime=#{addtime}
where
favid=#{favid}

mapper.select:
select a.* , b.goodsname, b.status, b.image , b.price from fav a ,
goods b where
a.goodsid = b.goodsid

	and a.usersid = #{usersid}


	and a.goodsid = #{goodsid}


	and a.addtime = #{addtime}
